import org.junit.Assert.*
import org.junit.Test

class Day2Test{


    @Test
    fun simpleInput1(){
        assertEquals("2", Day2.findCode(Day2.defaultSafe(), "U".split("\n")))
    }

    @Test
    fun simpleInput2(){
        assertEquals("5", Day2.findCode(Day2.defaultSafe(), "LR".split("\n")))
    }


    @Test
    fun simpleInput3(){
        assertEquals("4", Day2.findCode(Day2.defaultSafe(), "L".split("\n")))
    }

    @Test
    fun simpleInput4(){
        assertEquals("6", Day2.findCode(Day2.defaultSafe(), "R".split("\n")))
    }

    @Test
    fun simpleInput5(){
        assertEquals("8", Day2.findCode(Day2.defaultSafe(), "D".split("\n")))
    }


    @Test
    fun providedInput1(){
        assertEquals("1985", Day2.findCode(Day2.defaultSafe(), "ULL\nRRDDD\nLURDL\nUUUUD".split("\n")))
    }

    @Test
    fun testInput1(){
        assertEquals("45973", Day2.findCode(Day2.defaultSafe(), ("\n" +
                "\n" +
                "\n" +
                "\n" +
                "").split("\n")))
    }


    @Test
    fun testInput2(){
        assertEquals("45973", Day2.findCode(Day2.fancySafe(), ("\n" +
                "\n" +
                "\n" +
                "\n" +
                "").split("\n")))
    }



    @Test
    fun simpleFancyInput1(){
        assertEquals("5", Day2.findCode(Day2.fancySafe(), "".split("\n")))
    }

    @Test
    fun simpleFancyInput2(){
        assertEquals("5", Day2.findCode(Day2.fancySafe(), "L".split("\n")))
    }


    @Test
    fun simpleFancyInput3(){
        assertEquals("6", Day2.findCode(Day2.fancySafe(), "R".split("\n")))
    }

    @Test
    fun simpleFancyInput4(){
        assertEquals("2", Day2.findCode(Day2.fancySafe(), "RU".split("\n")))
    }

    @Test
    fun simpleFancyInput5(){
        assertEquals("A", Day2.findCode(Day2.fancySafe(), "RD".split("\n")))
    }


    @Test
    fun providedFancyInput1(){
        assertEquals("5DB3", Day2.findCode(Day2.fancySafe(), "ULL\nRRDDD\nLURDL\nUUUUD".split("\n")))
    }

}